-- Generate NSIS install stuff for scripts.local dir = require 'pl.dir'local path = require 'pl.path'local scripts = [[scripts]]for root, dirs, files in dir.walk(scripts) do    if not root:find('.svn') then        local p = root:sub(#scripts+1)        print('SetOutPath "$SCRIPTSDIR'..p..'"')        for _, f in ipairs(files) do            local ext = path.extension(f):sub(2)            if ext == 'lua' or ext == 'dll' or ext == 'png' then                print('    File "${XWORD_TRUNK}\\scripts'..path.join(p, f)..'"')            end        end    endend